import toast from 'react-hot-toast';
import { API_ENDPOINTS } from '../../config/api';
import { useAuth } from '../../contexts/AuthContext';

const LibraryPage = () => {
  const [books, setBooks] = useState([]);
  const [filteredBooks, setFilteredBooks] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedSubject, setSelectedSubject] = useState('all');
  const [selectedLanguage, setSelectedLanguage] = useState('all');
  const [selectedLevel, setSelectedLevel] = useState('all');

  const subjects = [
    { id: 'all', name: 'All Subjects' },
    { id: 'english', name: 'English' },
    { id: 'mathematics', name: 'Mathematics' },
    { id: 'science', name: 'Science' },
    { id: 'history', name: 'History' },
    { id: 'literature', name: 'Literature' },
    { id: 'geography', name: 'Geography' },
    { id: 'computer_science', name: 'Computer Science' },
    { id: 'languages', name: 'Languages' }
  ];

  const languages = [
    { id: 'all', name: 'All Languages' },
    { id: 'english', name: 'English' },
    { id: 'urdu', name: 'Urdu' },
    { id: 'arabic', name: 'Arabic' },
    { id: 'french', name: 'French' },
    { id: 'spanish', name: 'Spanish' }
  ];

  const levels = [
    { id: 'all', name: 'All Levels' },
    { id: 'beginner', name: 'Beginner' },
    { id: 'intermediate', name: 'Intermediate' },
    { id: 'advanced', name: 'Advanced' }
  ];

  useEffect(() => {
    fetchBooks();
  }, []);

  useEffect(() => {
    filterBooks();
  }, [books, searchTerm, selectedSubject, selectedLanguage, selectedLevel]);

  const fetchBooks = async () => {
    try {
      // Get auth token from localStorage
      const tokens = JSON.parse(localStorage.getItem('tokens') || '{}');
      const headers = {
        'Content-Type': 'application/json',
      };

      if (tokens.access) {
        headers['Authorization'] = `Bearer ${tokens.access}`;
      }

      const response = await fetch(API_ENDPOINTS.BOOKS, { headers });
      if (response.ok) {
        const data = await response.json();
        setBooks(data.results || []);
            } else if (response.status === 401) {
        // If unauthorized, show mock data for development
        console.log('Using mock data due to authentication required');
        setBooks([
          {
            id: 1,
            title: 'English Grammar Fundamentals',
            author: 'Dr. Sarah Johnson',
            subject: 'english',
            language: 'english',
            level: 'beginner',
            description: 'Master the basics of English grammar and vocabulary. Perfect for beginners starting their English learning journey.',
            cover_image: 'https://images.unsplash.com/photo-1544716278-ca5e3f4abd8c?w=400&h=500&fit=crop',
            chapters_count: 2,
            rating: 4.8,
            students_enrolled: 1250
          },
          {
            id: 2,
            title: 'Advanced English Composition',
            author: 'Prof. Michael Chen',
            subject: 'english',
            language: 'english',
            level: 'intermediate',
            description: 'Develop advanced writing skills and composition techniques. Learn to express ideas clearly and persuasively.',
            cover_image: 'https://images.unsplash.com/photo-1455390582262-044cdead277a?w=400&h=500&fit=crop',
            chapters_count: 2,
            rating: 4.6,
            students_enrolled: 980
          },
          {
            id: 3,
            title: 'Business English Communication',
            author: 'Dr. Emily Rodriguez',
            subject: 'english',
            language: 'english',
            level: 'intermediate',
            description: 'Learn professional English communication skills for business environments. Master presentations, meetings, and written correspondence.',
            cover_image: 'https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=400&h=500&fit=crop',
            chapters_count: 2,
            rating: 4.7,
            students_enrolled: 750
          },
          {
            id: 4,
            title: 'English Literature Classics',
            author: 'Prof. James Wilson',
            subject: 'literature',
            language: 'english',
            level: 'advanced',
            description: 'Explore classic English literature from Shakespeare to modern authors. Analyze themes, characters, and literary techniques.',
            cover_image: 'https://images.unsplash.com/photo-1481627834876-b7833e8f5570?w=400&h=500&fit=crop',
            chapters_count: 2,
            rating: 4.9,
            students_enrolled: 620
          }
        ]);
      }
    } catch (error) {
      console.error('Error fetching books:', error);
      toast.error('Failed to load books');
    } finally {
      setIsLoading(false);
    }
  };

  const filterBooks = () => {
    let filtered = books;

    // Filter by search term
    if (searchTerm) {
      filtered = filtered.filter(book =>
        book.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        book.author.toLowerCase().includes(searchTerm.toLowerCase()) ||
        book.description.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // Filter by subject
    if (selectedSubject !== 'all') {
      filtered = filtered.filter(book => book.subject === selectedSubject);
    }

    // Filter by language
    if (selectedLanguage !== 'all') {
      filtered = filtered.filter(book => book.language === selectedLanguage);
    }

    // Filter by level
    if (selectedLevel !== 'all') {
      filtered = filtered.filter(book => book.level === selectedLevel);
    }

    setFilteredBooks(filtered);
  };

  const getSubjectColor = (subject) => {
    const colors = {
      'english': 'bg-blue-100 text-blue-800 dark:bg-blue-900/20 dark:text-blue-200',
      'mathematics': 'bg-green-100 text-green-800 dark:bg-green-900/20 dark:text-green-200',
      'science': 'bg-purple-100 text-purple-800 dark:bg-purple-900/20 dark:text-purple-200',
      'history': 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900/20 dark:text-yellow-200',
      'literature': 'bg-pink-100 text-pink-800 dark:bg-pink-900/20 dark:text-pink-200',
      'geography': 'bg-indigo-100 text-indigo-800 dark:bg-indigo-900/20 dark:text-indigo-200',
      'computer_science': 'bg-cyan-100 text-cyan-800 dark:bg-cyan-900/20 dark:text-cyan-200',
      'languages': 'bg-orange-100 text-orange-800 dark:bg-orange-900/20 dark:text-orange-200'
    };
    return colors[subject] || 'bg-slate-100 text-slate-800 dark:bg-slate-900/20 dark:text-slate-200';
  };

  const getLevelColor = (level) => {
    const colors = {
      'beginner': 'bg-green-100 text-green-800 dark:bg-green-900/20 dark:text-green-200',
      'intermediate': 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900/20 dark:text-yellow-200',
      'advanced': 'bg-red-100 text-red-800 dark:bg-red-900/20 dark:text-red-200'
    };
    return colors[level] || 'bg-slate-100 text-slate-800 dark:bg-slate-900/20 dark:text-slate-200';
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center min-h-96">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-brand-600 mx-auto"></div>
          <p className="mt-4 text-base leading-relaxed text-slate-700 dark:text-slate-200">Loading books...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="bg-white dark:bg-slate-800 rounded-xl p-4 md:p-5 border border-slate-200 dark:border-slate-700 shadow-sm hover:shadow-md transition-all duration-200">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-xl md:text-2xl font-semibold text-slate-900 dark:text-slate-100">Library</h1>
            <p className="mt-2 text-base leading-relaxed text-slate-700 dark:text-slate-200">
              Explore our collection of educational books and resources
            </p>
          </div>
          <div className="flex items-center space-x-4">
            <span className="text-sm text-slate-500 dark:text-slate-400">
              {filteredBooks.length} of {books.length} books
            </span>
          </div>
        </div>
      </div>

      {/* Search and Filters */}
      <div className="bg-white dark:bg-slate-800 rounded-xl p-4 md:p-5 border border-slate-200 dark:border-slate-700 shadow-sm hover:shadow-md transition-all duration-200">
        <div className="grid grid-cols-1 lg:grid-cols-5 gap-4">
          {/* Search */}
          <div className="lg:col-span-2">
            <div className="relative">
              <MagnifyingGlassIcon className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-slate-400" />
              <input
                type="text"
                placeholder="Search books, authors, or topics..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full pl-10 pr-4 py-2 border border-slate-300 dark:border-slate-600 rounded-lg focus:ring-2 focus:ring-brand-500 focus:border-brand-500 bg-white dark:bg-slate-800 text-slate-900 dark:text-slate-100"
              />
            </div>
          </div>

          {/* Subject Filter */}
          <div>
            <select
              value={selectedSubject}
              onChange={(e) => setSelectedSubject(e.target.value)}
              className="w-full px-3 py-2 border border-slate-300 dark:border-slate-600 rounded-lg focus:ring-2 focus:ring-brand-500 focus:border-brand-500 bg-white dark:bg-slate-800 text-slate-900 dark:text-slate-100"
            >
              {subjects.map(subject => (
                <option key={subject.id} value={subject.id}>
                  {subject.name}
                </option>
              ))}
            </select>
          </div>

          {/* Language Filter */}
          <div>
            <select
              value={selectedLanguage}
              onChange={(e) => setSelectedLanguage(e.target.value)}
              className="w-full px-3 py-2 border border-slate-300 dark:border-slate-600 rounded-lg focus:ring-2 focus:ring-brand-500 focus:border-brand-500 bg-white dark:bg-slate-800 text-slate-900 dark:text-slate-100"
            >
              {languages.map(language => (
                <option key={language.id} value={language.id}>
                  {language.name}
                </option>
              ))}
            </select>
          </div>

          {/* Level Filter */}
          <div>
            <select
              value={selectedLevel}
              onChange={(e) => setSelectedLevel(e.target.value)}
              className="w-full px-3 py-2 border border-slate-300 dark:border-slate-600 rounded-lg focus:ring-2 focus:ring-brand-500 focus:border-brand-500 bg-white dark:bg-slate-800 text-slate-900 dark:text-slate-100"
            >
              {levels.map(level => (
                <option key={level.id} value={level.id}>
                  {level.name}
                </option>
              ))}
            </select>
          </div>
        </div>
      </div>

      {/* Books Grid */}
      {filteredBooks.length === 0 ? (
        <div className="text-center py-12">
          <BookOpenIcon className="mx-auto h-12 w-12 text-slate-400" />
          <h3 className="mt-2 text-base font-medium text-slate-900 dark:text-slate-100">No books found</h3>
          <p className="mt-1 text-base leading-relaxed text-slate-700 dark:text-slate-200">
            Try adjusting your search or filter criteria.
          </p>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
          {filteredBooks.map((book) => {
            // Skip rendering if book is undefined or null
            if (!book) return null;

            return (
              <div
                key={book.id || `book-${Math.random()}`}
                className="bg-white dark:bg-slate-800 rounded-xl shadow-sm border border-slate-200 dark:border-slate-700 overflow-hidden hover:shadow-md transition-all duration-200"
              >
              {/* Book Cover */}
              <div className="aspect-w-3 aspect-h-4 bg-slate-200">
                {book.cover_image ? (
                  <img
                    src={book.cover_image}
                    alt={book.title || 'Book Cover'}
                    className="w-full h-full object-cover"
                  />
                ) : (
                  <div className="w-full h-full flex items-center justify-center">
                    <BookOpenIcon className="h-12 w-12 text-slate-400" />
                  </div>
                )}
              </div>

              {/* Book Info */}
              <div className="p-4 md:p-5">
                <div className="flex items-start justify-between mb-2">
                  <h3 className="text-lg font-semibold text-slate-900 dark:text-slate-100 line-clamp-2">
                    {book.title || 'Untitled Book'}
                  </h3>
                  <div className="flex items-center space-x-1 ml-2">
                    <StarIconSolid className="h-4 w-4 text-yellow-400" />
                    <span className="text-sm text-slate-600 dark:text-slate-400">{book.rating || 'N/A'}</span>
                  </div>
                </div>

                <p className="text-sm text-slate-600 dark:text-slate-400 mb-3">
                  by {book.author || 'Unknown Author'}
                </p>

                <p className="text-base leading-relaxed text-slate-700 dark:text-slate-200 mb-4 line-clamp-3">
                  {book.description || 'No description available.'}
                </p>

                {/* Tags */}
                <div className="flex flex-wrap gap-2 mb-4">
                  <span className={`px-2 py-1 text-xs font-medium rounded-full ${getSubjectColor(book.subject)}`}>
                    {(book.subject || 'other').replace('_', ' ').toUpperCase()}
                  </span>
                  <span className={`px-2 py-1 text-xs font-medium rounded-full ${getLevelColor(book.level)}`}>
                    {(book.level || 'beginner').toUpperCase()}
                  </span>
                </div>

                {/* Stats */}
                <div className="flex items-center justify-between text-sm text-slate-600 dark:text-slate-400 mb-4">
                  <div className="flex items-center space-x-1">
                    <BookOpenIcon className="h-4 w-4" />
                    <span>{book.chapters_count || 0} chapters</span>
                  </div>
                  <div className="flex items-center space-x-1">
                    <UserGroupIcon className="h-4 w-4" />
                    <span>{(book.students_enrolled || 0).toLocaleString()}</span>
                  </div>
                </div>

                {/* Action Button */}
                <Link
                  to={`/app/library/book/${book.id || 'unknown'}`}
                  className="w-full bg-brand-600 text-white py-2 px-4 rounded-lg text-sm font-medium hover:bg-brand-700 focus:ring-2 focus:ring-brand-500 focus:ring-offset-2 transition-colors duration-200 text-center block"
                >
                  Start Reading
                </Link>
              </div>
            </div>
          );
        })}
        </div>
      )}
    </div>
  );
};

export default LibraryPage;
