import React, { useState } from 'react';
import { 
  ChevronDownIcon, 
  ChevronRightIcon,
  BookOpenIcon,
  AcademicCapIcon,
  QuestionMarkCircleIcon,
  LanguageIcon,
  DocumentTextIcon,
  ChatBubbleLeftRightIcon,
  PencilIcon,
  LightBulbIcon,
  ClipboardDocumentCheckIcon
} from '@heroicons/react/24/outline';

const ChapterSections = ({ sections = [], onSectionComplete, completedSections = [] }) => {
  const [expandedSections, setExpandedSections] = useState(new Set([0])); // Start with first section expanded

  const toggleSection = (index) => {
    const newExpanded = new Set(expandedSections);
    if (newExpanded.has(index)) {
      newExpanded.delete(index);
    } else {
      newExpanded.add(index);
    }
    setExpandedSections(newExpanded);
  };

  const getSectionIcon = (kind) => {
    if (!kind) return DocumentTextIcon;

    const iconMap = {
      'INTRODUCTION': BookOpenIcon,
      'POINT_WISE_SUMMARY': DocumentTextIcon,
      'SLOS': AcademicCapIcon,
      'PRE_READING_QUESTIONS': QuestionMarkCircleIcon,
      'VOCABULARY_TEXT_TRANSLATION': LanguageIcon,
      'GLOSSARY': DocumentTextIcon,
      'EXERCISE_SHORT_QUESTIONS': QuestionMarkCircleIcon,
      'EXERCISE_OBJECTIVES': ClipboardDocumentCheckIcon,
      'EXERCISE_VOCABULARY_GRAMMAR': AcademicCapIcon,
      'EXERCISE_ORAL_COMMUNICATION': ChatBubbleLeftRightIcon,
      'EXERCISE_WRITING_SKILLS': PencilIcon,
      'IDIOMS_PHRASAL_VERBS': LightBulbIcon,
      'ASSESSMENT_TEST': ClipboardDocumentCheckIcon,
    };
    return iconMap[kind] || DocumentTextIcon;
  };

  const getSectionColor = (kind) => {
    if (!kind) return 'bg-slate-50 border-slate-200 text-slate-800';

    const colorMap = {
      'INTRODUCTION': 'bg-blue-50 border-blue-200 text-blue-800',
      'POINT_WISE_SUMMARY': 'bg-green-50 border-green-200 text-green-800',
      'SLOS': 'bg-purple-50 border-purple-200 text-purple-800',
      'PRE_READING_QUESTIONS': 'bg-yellow-50 border-yellow-200 text-yellow-800',
      'VOCABULARY_TEXT_TRANSLATION': 'bg-indigo-50 border-indigo-200 text-indigo-800',
      'GLOSSARY': 'bg-teal-50 border-teal-200 text-teal-800',
      'EXERCISE_SHORT_QUESTIONS': 'bg-orange-50 border-orange-200 text-orange-800',
      'EXERCISE_OBJECTIVES': 'bg-red-50 border-red-200 text-red-800',
      'EXERCISE_VOCABULARY_GRAMMAR': 'bg-pink-50 border-pink-200 text-pink-800',
      'EXERCISE_ORAL_COMMUNICATION': 'bg-emerald-50 border-emerald-200 text-emerald-800',
      'EXERCISE_WRITING_SKILLS': 'bg-cyan-50 border-cyan-200 text-cyan-800',
      'IDIOMS_PHRASAL_VERBS': 'bg-amber-50 border-amber-200 text-amber-800',
      'ASSESSMENT_TEST': 'bg-rose-50 border-rose-200 text-rose-800',
    };
    return colorMap[kind] || 'bg-slate-50 border-slate-200 text-slate-800';
  };

  const getSectionTitle = (kind) => {
    if (!kind) return 'Unknown Section';

    const titleMap = {
      'INTRODUCTION': 'Introduction',
      'POINT_WISE_SUMMARY': 'Point-wise Summary',
      'SLOS': 'Student Learning Objectives',
      'PRE_READING_QUESTIONS': 'Pre-reading Questions',
      'VOCABULARY_TEXT_TRANSLATION': 'Vocabulary, Text & Translation',
      'GLOSSARY': 'Glossary',
      'EXERCISE_SHORT_QUESTIONS': 'Exercise: Short Questions',
      'EXERCISE_OBJECTIVES': 'Exercise: Objectives',
      'EXERCISE_VOCABULARY_GRAMMAR': 'Exercise: Vocabulary and Grammar',
      'EXERCISE_ORAL_COMMUNICATION': 'Exercise: Oral Communication',
      'EXERCISE_WRITING_SKILLS': 'Exercise: Writing Skills',
      'IDIOMS_PHRASAL_VERBS': 'Idioms and Phrasal Verbs of the Lesson',
      'ASSESSMENT_TEST': 'Assessment Test',
    };
    return titleMap[kind] || (typeof kind === 'string' ? kind.replace('_', ' ') : 'Unknown Section');
  };

  const renderSectionContent = (section) => {
    if (!section || !section.kind || !section.content) {
      return (
        <div className="text-slate-600 dark:text-slate-400 p-4">
          <p className="text-base leading-relaxed text-slate-700 dark:text-slate-200">Section content not available.</p>
        </div>
      );
    }

    const { kind, content } = section;

    switch (kind) {
      case 'INTRODUCTION':
        return (
          <div className="space-y-4">
            {content.text && (
              <p className="text-slate-700 leading-relaxed">{content.text}</p>
            )}
            {content.overview && (
              <div className="bg-blue-50 dark:bg-blue-900/20 p-4 rounded-lg">
                <h4 className="text-xl md:text-2xl font-semibold mb-3 text-blue-900 dark:text-blue-100">Overview</h4>
                <p className="text-base leading-relaxed text-slate-700 dark:text-slate-200">{content.overview}</p>
              </div>
            )}
            {content.key_concepts && (
              <div>
                <h4 className="text-xl md:text-2xl font-semibold mb-3 text-slate-900 dark:text-slate-100">Key Concepts</h4>
                <ul className="list-disc pl-6 space-y-1 text-base leading-relaxed text-slate-700 dark:text-slate-200">
                  {content.key_concepts.map((concept, index) => (
                    <li key={index}>{concept}</li>
                  ))}
                </ul>
              </div>
            )}
          </div>
        );

      case 'POINT_WISE_SUMMARY':
        return (
          <div className="space-y-4">
            {content.points && (
              <div>
                <h4 className="text-xl md:text-2xl font-semibold mb-3 text-slate-900 dark:text-slate-100">Main Points</h4>
                <ul className="list-disc pl-6 space-y-1 text-base leading-relaxed text-slate-700 dark:text-slate-200">
                  {content.points.map((point, index) => (
                    <li key={index}>{point}</li>
                  ))}
                </ul>
              </div>
            )}
            {content.main_ideas && (
              <div>
                <h4 className="text-xl md:text-2xl font-semibold mb-3 text-slate-900 dark:text-slate-100">Key Ideas</h4>
                <ul className="list-disc pl-6 space-y-1 text-base leading-relaxed text-slate-700 dark:text-slate-200">
                  {content.main_ideas.map((idea, index) => (
                    <li key={index}>{idea}</li>
                  ))}
                </ul>
              </div>
            )}
          </div>
        );

      case 'SLOS':
        return (
          <div className="space-y-4">
            {content.objectives && (
              <div>
                <h4 className="text-xl md:text-2xl font-semibold mb-3 text-slate-900 dark:text-slate-100">Learning Objectives</h4>
                <ul className="list-disc pl-6 space-y-1 text-base leading-relaxed text-slate-700 dark:text-slate-200">
                  {content.objectives.map((objective, index) => (
                    <li key={index}>{objective}</li>
                  ))}
                </ul>
              </div>
            )}
            {content.outcomes && (
              <div>
                <h4 className="text-xl md:text-2xl font-semibold mb-3 text-slate-900 dark:text-slate-100">Expected Outcomes</h4>
                <ul className="list-disc pl-6 space-y-1 text-base leading-relaxed text-slate-700 dark:text-slate-200">
                  {content.outcomes.map((outcome, index) => (
                    <li key={index}>{outcome}</li>
                  ))}
                </ul>
              </div>
            )}
          </div>
        );

      case 'PRE_READING_QUESTIONS':
        return (
          <div className="space-y-4">
            {content.questions && (
              <div>
                <h4 className="text-xl md:text-2xl font-semibold mb-3 text-slate-900 dark:text-slate-100">Pre-reading Questions</h4>
                <ul className="list-disc pl-6 space-y-1 text-base leading-relaxed text-slate-700 dark:text-slate-200">
                  {content.questions.map((question, index) => (
                    <li key={index}>{question}</li>
                  ))}
                </ul>
              </div>
            )}
            {content.purpose && (
              <div className="bg-yellow-50 dark:bg-yellow-900/20 p-4 rounded-lg">
                <h4 className="text-xl md:text-2xl font-semibold mb-3 text-yellow-900 dark:text-yellow-100">Purpose</h4>
                <p className="text-base leading-relaxed text-slate-700 dark:text-slate-200">{content.purpose}</p>
              </div>
            )}
          </div>
        );

      case 'VOCABULARY_TEXT_TRANSLATION':
        return (
          <div className="space-y-4">
            {content.vocabulary && (
              <div>
                <h4 className="text-xl md:text-2xl font-semibold mb-3 text-slate-900 dark:text-slate-100">Key Vocabulary</h4>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                  {Object.entries(content.vocabulary).map(([word, definition]) => (
                    <div key={word} className="bg-indigo-50 dark:bg-indigo-900/20 p-3 rounded-lg">
                      <span className="font-medium text-indigo-900 dark:text-indigo-100">{word}</span>
                      <p className="text-base leading-relaxed text-slate-700 dark:text-slate-200 text-sm mt-1">{definition}</p>
                    </div>
                  ))}
                </div>
              </div>
            )}
            {content.text && (
              <div>
                <h4 className="text-xl md:text-2xl font-semibold mb-3 text-slate-900 dark:text-slate-100">Main Text</h4>
                <div className="bg-slate-50 dark:bg-slate-800/50 p-4 rounded-lg">
                  <p className="text-base leading-relaxed text-slate-700 dark:text-slate-200">{content.text}</p>
                </div>
              </div>
            )}
            {content.translation && (
              <div>
                <h4 className="text-xl md:text-2xl font-semibold mb-3 text-slate-900 dark:text-slate-100">Translation</h4>
                <div className="bg-green-50 dark:bg-green-900/20 p-4 rounded-lg">
                  <p className="text-base leading-relaxed text-slate-700 dark:text-slate-200">{content.translation}</p>
                </div>
              </div>
            )}
          </div>
        );

      case 'GLOSSARY':
        return (
          <div className="space-y-4">
            {content.terms && (
              <div>
                <h4 className="text-xl md:text-2xl font-semibold mb-3 text-slate-900 dark:text-slate-100">Key Terms</h4>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                  {Object.entries(content.terms).map(([term, definition]) => (
                    <div key={term} className="bg-teal-50 dark:bg-teal-900/20 p-3 rounded-lg">
                      <span className="font-medium text-teal-900 dark:text-teal-100">{term}</span>
                      <p className="text-base leading-relaxed text-slate-700 dark:text-slate-200 text-sm mt-1">{definition}</p>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
        );

      case 'EXERCISE_SHORT_QUESTIONS':
        return (
          <div>
            <h4 className="text-xl md:text-2xl font-semibold mb-3 text-slate-900 dark:text-slate-100">Short Answer Questions</h4>
            <div className="space-y-4">
              {content.questions && content.questions.map((question, index) => (
                <div key={index} className="bg-orange-50 dark:bg-orange-900/20 p-4 rounded-lg">
                  <h5 className="text-lg font-semibold text-orange-900 dark:text-orange-100 mb-2">
                    Question {index + 1}
                  </h5>
                  <p className="text-base leading-relaxed text-slate-700 dark:text-slate-200 mb-3">{question}</p>
                  {content.answers && content.answers[question] && (
                    <div className="bg-white dark:bg-slate-800 p-3 rounded border border-orange-200 dark:border-orange-700">
                      <p className="text-sm text-slate-700 dark:text-slate-200">
                        <strong>Answer:</strong> {content.answers[question]}
                      </p>
                    </div>
                  )}
                </div>
              ))}
            </div>
          </div>
        );

      case 'EXERCISE_OBJECTIVES':
        return (
          <div>
            <h4 className="text-xl md:text-2xl font-semibold mb-3 text-slate-900 dark:text-slate-100">Objective Questions</h4>
            <div className="space-y-4">
              {content.questions && content.questions.map((question, index) => (
                <div key={index} className="bg-red-50 dark:bg-red-900/20 p-4 rounded-lg">
                  <h5 className="text-lg font-semibold text-red-900 dark:text-red-100 mb-3">
                    Question {index + 1}
                  </h5>
                  <p className="text-base leading-relaxed text-slate-700 dark:text-slate-200 mb-3">{question.question}</p>
                  <div className="space-y-2">
                    {question.options && question.options.map((option, optIndex) => (
                      <label key={optIndex} className="flex items-center">
                        <input
                          type="radio"
                          name={`objective-${index}`}
                          value={optIndex}
                          className="mr-2 text-red-600"
                          disabled
                        />
                        <span className={`text-sm ${
                          optIndex === question.correct_answer 
                            ? 'text-green-700 dark:text-green-300 font-medium' 
                            : 'text-slate-700 dark:text-slate-200'
                        }`}>
                          {option}
                          {optIndex === question.correct_answer && ' âœ“'}
                        </span>
                      </label>
                    ))}
                  </div>
                </div>
              ))}
            </div>
          </div>
        );

      case 'EXERCISE_VOCABULARY_GRAMMAR':
        return (
          <div className="space-y-4">
            {content.vocabulary_exercises && (
              <div>
                <h4 className="text-xl md:text-2xl font-semibold mb-3 text-slate-900 dark:text-slate-100">Vocabulary Exercises</h4>
                <ul className="list-disc pl-6 space-y-1 text-base leading-relaxed text-slate-700 dark:text-slate-200">
                  {content.vocabulary_exercises.map((exercise, index) => (
                    <li key={index}>{exercise}</li>
                  ))}
                </ul>
              </div>
            )}
            {content.grammar_exercises && (
              <div>
                <h4 className="text-xl md:text-2xl font-semibold mb-3 text-slate-900 dark:text-slate-100">Grammar Exercises</h4>
                <ul className="list-disc pl-6 space-y-1 text-base leading-relaxed text-slate-700 dark:text-slate-200">
                  {content.grammar_exercises.map((exercise, index) => (
                    <li key={index}>{exercise}</li>
                  ))}
                </ul>
              </div>
            )}
          </div>
        );

      case 'EXERCISE_ORAL_COMMUNICATION':
        return (
          <div className="space-y-4">
            {content.speaking_activities && (
              <div>
                <h4 className="text-xl md:text-2xl font-semibold mb-3 text-slate-900 dark:text-slate-100">Speaking Activities</h4>
                <ul className="list-disc pl-6 space-y-1 text-base leading-relaxed text-slate-700 dark:text-slate-200">
                  {content.speaking_activities.map((activity, index) => (
                    <li key={index}>{activity}</li>
                  ))}
                </ul>
              </div>
            )}
            {content.discussion_topics && (
              <div>
                <h4 className="text-xl md:text-2xl font-semibold mb-3 text-slate-900 dark:text-slate-100">Discussion Topics</h4>
                <ul className="list-disc pl-6 space-y-1 text-base leading-relaxed text-slate-700 dark:text-slate-200">
                  {content.discussion_topics.map((topic, index) => (
                    <li key={index}>{topic}</li>
                  ))}
                </ul>
              </div>
            )}
          </div>
        );

      case 'EXERCISE_WRITING_SKILLS':
        return (
          <div className="space-y-4">
            {content.writing_prompts && (
              <div>
                <h4 className="text-xl md:text-2xl font-semibold mb-3 text-slate-900 dark:text-slate-100">Writing Prompts</h4>
                <ul className="list-disc pl-6 space-y-1 text-base leading-relaxed text-slate-700 dark:text-slate-200">
                  {content.writing_prompts.map((prompt, index) => (
                    <li key={index}>{prompt}</li>
                  ))}
                </ul>
              </div>
            )}
            {content.writing_tasks && (
              <div>
                <h4 className="text-xl md:text-2xl font-semibold mb-3 text-slate-900 dark:text-slate-100">Writing Tasks</h4>
                <ul className="list-disc pl-6 space-y-1 text-base leading-relaxed text-slate-700 dark:text-slate-200">
                  {content.writing_tasks.map((task, index) => (
                    <li key={index}>{task}</li>
                  ))}
                </ul>
              </div>
            )}
          </div>
        );

      case 'IDIOMS_PHRASAL_VERBS':
        return (
          <div className="space-y-4">
            {content.idioms && (
              <div>
                <h4 className="text-xl md:text-2xl font-semibold mb-3 text-slate-900 dark:text-slate-100">Idioms</h4>
                <div className="space-y-3">
                  {Object.entries(content.idioms).map(([idiom, meaning]) => (
                    <div key={idiom} className="bg-amber-50 dark:bg-amber-900/20 p-4 rounded-lg">
                      <h5 className="text-lg font-semibold text-amber-900 dark:text-amber-100">{idiom}</h5>
                      <p className="text-base leading-relaxed text-slate-700 dark:text-slate-200 text-sm mt-1">{meaning}</p>
                    </div>
                  ))}
                </div>
              </div>
            )}
            {content.phrasal_verbs && (
              <div>
                <h4 className="text-xl md:text-2xl font-semibold mb-3 text-slate-900 dark:text-slate-100">Phrasal Verbs</h4>
                <div className="space-y-3">
                  {Object.entries(content.phrasal_verbs).map(([phrasal, meaning]) => (
                    <div key={phrasal} className="bg-amber-50 dark:bg-amber-900/20 p-4 rounded-lg">
                      <h5 className="text-lg font-semibold text-amber-900 dark:text-amber-100">{phrasal}</h5>
                      <p className="text-base leading-relaxed text-slate-700 dark:text-slate-200 text-sm mt-1">{meaning}</p>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
        );

      case 'ASSESSMENT_TEST':
        return (
          <div className="space-y-4">
            {content.time_limit && content.total_marks && (
              <div className="bg-rose-50 dark:bg-rose-900/20 p-4 rounded-lg">
                <h4 className="text-xl md:text-2xl font-semibold mb-3 text-rose-900 dark:text-rose-100">Test Information</h4>
                <div className="grid grid-cols-2 gap-4 text-sm">
                  <div>
                    <span className="text-rose-700 dark:text-rose-300 font-medium">Time Limit:</span>
                    <p className="text-base leading-relaxed text-slate-700 dark:text-slate-200">{content.time_limit}</p>
                  </div>
                  <div>
                    <span className="text-rose-700 dark:text-rose-300 font-medium">Total Marks:</span>
                    <p className="text-base leading-relaxed text-slate-700 dark:text-slate-200">{content.total_marks}</p>
                  </div>
                </div>
              </div>
            )}
            {content.questions && (
              <div>
                <h4 className="text-xl md:text-2xl font-semibold mb-3 text-slate-900 dark:text-slate-100">Assessment Questions</h4>
                <div className="space-y-4">
                  {content.questions.map((question, index) => (
                    <div key={index} className="bg-white dark:bg-slate-800 border border-rose-200 dark:border-rose-700 p-4 rounded-lg">
                      <h5 className="text-lg font-semibold text-slate-900 dark:text-slate-100 mb-3">
                        Question {index + 1}
                      </h5>
                      <p className="text-base leading-relaxed text-slate-700 dark:text-slate-200 mb-3">{question.question}</p>
                      {question.options && (
                        <div className="space-y-2">
                          {question.options.map((option, optIndex) => (
                            <label key={optIndex} className="flex items-center">
                              <input
                                type="radio"
                                name={`assessment-${index}`}
                                value={optIndex}
                                className="mr-2 text-rose-600"
                                disabled
                              />
                              <span className="text-base leading-relaxed text-slate-700 dark:text-slate-200">{option}</span>
                            </label>
                          ))}
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
        );

      default:
        return (
          <div className="text-slate-600 dark:text-slate-400">
            <p className="text-base leading-relaxed text-slate-700 dark:text-slate-200">Content type "{kind}" not yet implemented.</p>
            <pre className="mt-2 text-xs bg-slate-100 dark:bg-slate-800 p-2 rounded overflow-auto text-slate-700 dark:text-slate-200">
              {JSON.stringify(content, null, 2)}
            </pre>
          </div>
        );
    }
  };

  if (!sections || !Array.isArray(sections) || sections.length === 0) {
    return (
      <div className="text-center py-8">
        <BookOpenIcon className="mx-auto h-12 w-12 text-slate-400 dark:text-slate-500" />
        <h3 className="mt-2 text-sm font-medium text-slate-900 dark:text-slate-100">No sections available</h3>
        <p className="mt-1 text-sm text-slate-500 dark:text-slate-400">
          This chapter doesn't have any sections yet.
        </p>
      </div>
    );
  }

  return (
    <div className="space-y-4">
      <div className="mb-6">
        <h2 className="text-2xl font-bold text-slate-900 dark:text-slate-100 mb-2">Chapter Content</h2>
        <p className="text-base leading-relaxed text-slate-700 dark:text-slate-200">
          This chapter contains {sections.length} structured sections covering all aspects of English language learning.
        </p>
      </div>

      {sections.map((section, index) => {
        // Safety check for section structure
        if (!section || typeof section !== 'object') {
          console.warn('Invalid section at index:', index, section);
          return null;
        }

        const IconComponent = getSectionIcon(section.kind);
        const isExpanded = expandedSections.has(index);
        const isCompleted = completedSections.includes(section.id);
        const sectionColor = getSectionColor(section.kind);

        return (
          <div key={section.id || index} className="bg-white dark:bg-slate-800 border border-slate-200 dark:border-slate-700 rounded-lg shadow-sm">
            <button
              onClick={() => toggleSection(index)}
              className="w-full px-6 py-4 text-left focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-inset"
            >
              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-3">
                  <div className={`p-2 rounded-lg ${sectionColor}`}>
                    <IconComponent className="h-5 w-5" />
                  </div>
                  <div>
                    <h3 className="text-lg font-semibold text-slate-900 dark:text-slate-100">
                      {getSectionTitle(section.kind)}
                    </h3>
                    {section.title && (
                      <p className="text-sm text-slate-600 dark:text-slate-400 mt-1">{section.title}</p>
                    )}
                  </div>
                </div>
                <div className="flex items-center space-x-2">
                  {isCompleted && (
                    <div className="w-6 h-6 bg-green-100 text-green-600 rounded-full flex items-center justify-center">
                      <svg className="w-4 h-4" fill="currentColor" viewBox="0 0 20 20">
                        <path fillRule="evenodd" d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z" clipRule="evenodd" />
                      </svg>
                    </div>
                  )}
                  {isExpanded ? (
                    <ChevronDownIcon className="h-5 w-5 text-slate-400 dark:text-slate-500" />
                  ) : (
                    <ChevronRightIcon className="h-5 w-5 text-slate-400 dark:text-slate-500" />
                  )}
                </div>
              </div>
            </button>

            {isExpanded && (
              <div className="px-6 pb-6 border-t border-slate-200 dark:border-slate-700">
                <div className="pt-4">
                  {renderSectionContent(section)}
                </div>

                <div className="mt-6 pt-4 border-t border-slate-200 dark:border-slate-700">
                  <button
                    onClick={() => onSectionComplete && onSectionComplete(section.id, index)}
                    className="bg-brand-600 text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-brand-700 transition-colors duration-200"
                  >
                    {isCompleted ? 'Completed âœ“' : 'Mark as Complete'}
                  </button>
                </div>
              </div>
            )}
          </div>
        );
      })}
    </div>
  );
};

export default ChapterSections;
