sourcesContent:
import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { 
  BookOpenIcon, 
  AcademicCapIcon, 
  ClockIcon, 
  StarIcon,
  UserIcon,
  CalendarIcon,
  GlobeAltIcon,
  PlayIcon,
  DocumentTextIcon,
  ChartBarIcon
} from '@heroicons/react/24/outline';
import toast from 'react-hot-toast';
import { API_ENDPOINTS } from '../../config/api';

const BookDetailPage = () => {
  const { bookId } = useParams();
  const [book, setBook] = useState(null);
  const [chapters, setChapters] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('overview');

  useEffect(() => {
    fetchBookDetails();
    fetchChapters();
  }, [bookId]);

  const fetchBookDetails = async () => {
    try {
      // Get auth token from localStorage
      const tokens = JSON.parse(localStorage.getItem('tokens') || '{}');
      const headers = {
        'Content-Type': 'application/json',
      };

      if (tokens.access) {
        headers['Authorization'] = `Bearer ${tokens.access}`;
      }

      const response = await fetch(`${API_ENDPOINTS.BOOKS}${bookId}/`, { headers });
      if (response.ok) {
        const data = await response.json();
        setBook(data);
      } else if (response.status === 401) {
        // If unauthorized, show mock data for development
        console.log('Using mock data due to authentication required');

        // Dynamic mock data based on book ID
        const mockBookData = {
          1: {
            id: bookId,
            title: 'English Grammar Fundamentals',
            author: 'Dr. Sarah Johnson',
            subject: 'english',
            language: 'english',
            level: 'beginner',
            description: 'Master the basics of English grammar and vocabulary. Perfect for beginners starting their English learning journey.',
            long_description: 'English Grammar Fundamentals provides a solid foundation in English grammar, covering essential concepts like parts of speech, sentence structure, verb tenses, and basic vocabulary. This book is designed for beginners who want to build a strong grammatical foundation.',
            cover_image: 'https://images.unsplash.com/photo-1544716278-ca5e3f4abd8c?w=400&h=500&fit=crop',
            chapters_count: 2,
            total_pages: 200,
            rating: 4.8,
            students_enrolled: 1250,
            estimated_hours: 40,
            last_updated: '2024-01-15',
            tags: ['grammar', 'vocabulary', 'beginners', 'english fundamentals'],
            prerequisites: ['Basic reading skills', 'Interest in learning English'],
            learning_objectives: [
              'Understand basic English grammar rules',
              'Build essential vocabulary',
              'Master sentence structure',
              'Develop confidence in English'
            ]
          },
          2: {
            id: bookId,
            title: 'Advanced English Composition',
            author: 'Prof. Michael Chen',
            subject: 'english',
            language: 'english',
            level: 'intermediate',
            description: 'Develop advanced writing skills and composition techniques. Learn to express ideas clearly and persuasively.',
            long_description: 'Advanced English Composition focuses on developing sophisticated writing skills, including essay structure, argumentative writing, creative composition, and academic writing. Perfect for intermediate learners looking to enhance their writing abilities.',
            cover_image: 'https://images.unsplash.com/photo-1455390582262-044cdead277a?w=400&h=500&fit=crop',
            chapters_count: 2,
            total_pages: 300,
            rating: 4.6,
            students_enrolled: 980,
            estimated_hours: 60,
            last_updated: '2024-01-15',
            tags: ['composition', 'writing', 'essays', 'academic writing'],
            prerequisites: ['Basic English grammar', 'Intermediate writing skills'],
            learning_objectives: [
              'Master essay writing techniques',
              'Develop persuasive writing skills',
              'Learn academic writing standards',
              'Enhance creative composition'
            ]
          },
          11: {
            id: bookId,
            title: 'Master English: Complete Language Course',
            author: 'Dr. Emily Rodriguez',
            subject: 'english',
            language: 'english',
            level: 'intermediate',
            description: 'A comprehensive English course covering all aspects of language learning including grammar, vocabulary, communication skills, and practical exercises.',
            long_description: 'Master English is a comprehensive course that covers all essential aspects of English language learning. From fundamental grammar to advanced communication skills, this book provides a complete learning experience with practical exercises and real-world applications.',
            cover_image: 'https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=400&h=500&fit=crop',
            chapters_count: 3,
            total_pages: 400,
            rating: 4.9,
            students_enrolled: 2100,
            estimated_hours: 80,
            last_updated: '2024-01-15',
            tags: ['comprehensive', 'communication', 'grammar', 'vocabulary', 'exercises'],
            prerequisites: ['Basic English knowledge', 'Commitment to learning'],
            learning_objectives: [
              'Master comprehensive English skills',
              'Develop effective communication',
              'Build advanced vocabulary',
              'Apply skills in real situations'
            ]
          }
        };

        // Use the appropriate mock data based on book ID, or default to first book
        const bookData = mockBookData[parseInt(bookId)] || mockBookData[1];
        setBook(bookData);
      } else {
        toast.error('Failed to load book details');
      }
    } catch (error) {
      console.error('Error fetching book details:', error);
      toast.error('Failed to load book details');
    }
  };

  const fetchChapters = async () => {
    try {
      // Get auth token from localStorage
      const tokens = JSON.parse(localStorage.getItem('tokens') || '{}');
      const headers = {
        'Content-Type': 'application/json',
      };

      if (tokens.access) {
        headers['Authorization'] = `Bearer ${tokens.access}`;
      }

      const response = await fetch(`${API_ENDPOINTS.BOOKS}${bookId}/chapters/`, { headers });
      if (response.ok) {
        const data = await response.json();
        setChapters(data.results || []);
      } else if (response.status === 401) {
        // If unauthorized, show mock data for development
        console.log('Using mock data due to authentication required');

        // Dynamic chapters data based on book ID
        const mockChaptersData = {
          1: [ // English Grammar Fundamentals
            {
              id: 1,
              title: 'Basic Grammar Concepts',
              description: 'Introduction to parts of speech and sentence structure',
              duration: '4 hours',
              sections_count: 13,
              is_completed: false,
              progress: 0
            },
            {
              id: 2,
              title: 'Essential Vocabulary Building',
              description: 'Building a strong foundation of English vocabulary',
              duration: '3 hours',
              sections_count: 13,
              is_completed: false,
              progress: 0
            }
          ],
          2: [ // Advanced English Composition
            {
              id: 1,
              title: 'Essay Writing Fundamentals',
              description: 'Learn the basics of essay structure and organization',
              duration: '5 hours',
              sections_count: 13,
              is_completed: false,
              progress: 0
            },
            {
              id: 2,
              title: 'Advanced Writing Techniques',
              description: 'Master sophisticated writing styles and techniques',
              duration: '6 hours',
              sections_count: 13,
              is_completed: false,
              progress: 0
            }
          ],
          11: [ // Master English: Complete Language Course
            {
              id: 1,
              title: 'English Fundamentals: Building Strong Foundations',
              description: 'Master the basics of English grammar and vocabulary',
              duration: '6 hours',
              sections_count: 13,
              is_completed: false,
              progress: 0
            },
            {
              id: 2,
              title: 'Effective Communication: Speaking with Confidence',
              description: 'Develop your speaking and listening skills',
              duration: '8 hours',
              sections_count: 13,
              is_completed: false,
              progress: 0
            },
            {
              id: 3,
              title: 'Advanced Writing: Expressing Ideas Clearly',
              description: 'Learn to write clearly and persuasively',
              duration: '10 hours',
              sections_count: 13,
              is_completed: false,
              progress: 0
            }
          ]
        };

        // Use the appropriate chapters data based on book ID, or default to first book
        const chaptersData = mockChaptersData[parseInt(bookId)] || mockChaptersData[1];
        setChapters(chaptersData);
      } else {
        toast.error('Failed to load chapters');
      }
    } catch (error) {
      console.error('Error fetching chapters:', error);
      toast.error('Failed to load chapters');
    } finally {
      setIsLoading(false);
    }
  };

  const getSubjectIcon = (subject) => {
    switch (subject) {
      case 'english':
        return <BookOpenIcon className="h-6 w-6 text-indigo-600 dark:text-indigo-400" />;
      case 'mathematics':
        return <AcademicCapIcon className="h-6 w-6 text-blue-600 dark:text-blue-400" />;
      case 'science':
        return <AcademicCapIcon className="h-6 w-6 text-green-600 dark:text-green-400" />;
      case 'history':
        return <BookOpenIcon className="h-6 w-6 text-amber-600 dark:text-amber-400" />;
      case 'computer_science':
        return <AcademicCapIcon className="h-6 w-6 text-purple-600 dark:text-purple-400" />;
      default:
        return <BookOpenIcon className="h-6 w-6 text-slate-600 dark:text-slate-400" />;
    }
  };

  const getLevelBadge = (level) => {
    const colors = {
      beginner: 'bg-green-100 text-green-800 dark:bg-green-900/20 dark:text-green-200',
      intermediate: 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900/20 dark:text-yellow-200',
      advanced: 'bg-red-100 text-red-800 dark:bg-red-900/20 dark:text-red-200'
    };

    return (
      <span className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium ${colors[level] || colors.beginner}`}>
        {(level || 'beginner').charAt(0).toUpperCase() + (level || 'beginner').slice(1)} Level
      </span>
    );
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center min-h-96">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-brand-600 mx-auto"></div>
          <p className="mt-4 text-base leading-relaxed text-slate-700 dark:text-slate-200">Loading book details...</p>
        </div>
      </div>
    );
  }

  if (!book) {
    return (
      <div className="flex items-center justify-center min-h-96">
        <div className="text-center">
          <BookOpenIcon className="mx-auto h-12 w-12 text-slate-400" />
          <h3 className="mt-2 text-base font-medium text-slate-900 dark:text-slate-100">Book not found</h3>
          <p className="mt-1 text-base leading-relaxed text-slate-700 dark:text-slate-200">
            The book you're looking for doesn't exist.
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="bg-white dark:bg-slate-800 rounded-xl p-4 md:p-5 border border-slate-200 dark:border-slate-700 shadow-sm hover:shadow-md transition-all duration-200">
        <div className="flex items-center space-x-4">
          <Link
            to="/app/library"
            className="text-brand-600 hover:text-brand-500 dark:text-brand-400 dark:hover:text-brand-300"
          >
             Back to Library
          </Link>
          <span className="text-slate-400">/</span>
          <span className="text-base leading-relaxed text-slate-700 dark:text-slate-200">{book.title || 'Untitled Book'}</span>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Main Content */}
        <div className="lg:col-span-2 space-y-6">
          {/* Book Header */}
          <div className="bg-white dark:bg-slate-800 rounded-xl p-4 md:p-5 border border-slate-200 dark:border-slate-700 shadow-sm hover:shadow-md transition-all duration-200">
            <div className="flex items-start space-x-6">
              {book.cover_image ? (
                <img
                  src={book.cover_image}
                  alt={book.title || 'Book Cover'}
                  className="w-32 h-40 object-cover rounded-lg shadow-md"
                />
              ) : (
                <div className="w-32 h-40 bg-slate-200 dark:bg-slate-700 rounded-lg flex items-center justify-center">
                  <BookOpenIcon className="h-12 w-12 text-slate-400" />
                </div>
              )}
              <div className="flex-1">
                <div className="flex items-center space-x-3 mb-3">
                  {getSubjectIcon(book.subject)}
                  <span className="text-sm font-medium text-slate-500 dark:text-slate-400 uppercase">
                    {(book.subject || 'other').replace('_', ' ')}
                  </span>
                  {getLevelBadge(book.level)}
                </div>

                <h1 className="text-xl md:text-2xl font-semibold text-slate-900 dark:text-slate-100 mb-2">
                  {book.title || 'Untitled Book'}
                </h1>

                <p className="text-lg text-base leading-relaxed text-slate-700 dark:text-slate-200 mb-4">
                  by {book.author || 'Unknown Author'}
                </p>

                <p className="text-base leading-relaxed text-slate-700 dark:text-slate-200 mb-4">
                  {book.description || 'No description available.'}
                </p>

                {/* Stats */}
                <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                  <div className="text-center">
                    <div className="text-2xl font-bold text-brand-600 dark:text-brand-400">{book.chapters_count || 0}</div>
                    <div className="text-sm text-slate-500 dark:text-slate-400">Chapters</div>
                  </div>
                  <div className="text-center">
                    <div className="text-2xl font-bold text-brand-600 dark:text-brand-400">{book.estimated_hours || 0}</div>
                    <div className="text-sm text-slate-500 dark:text-slate-400">Hours</div>
                  </div>
                  <div className="text-center">
                    <div className="text-2xl font-bold text-brand-600 dark:text-brand-400">{(book.students_enrolled || 0).toLocaleString()}</div>
                    <div className="text-sm text-slate-500 dark:text-slate-400">Students</div>
                  </div>
                  <div className="text-center">
                    <div className="text-2xl font-bold text-brand-600 dark:text-brand-400">{book.rating || 'N/A'}</div>
                    <div className="text-sm text-slate-500 dark:text-slate-400">Rating</div>
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* Tabs */}
          <div className="bg-white dark:bg-slate-800 rounded-xl border border-slate-200 dark:border-slate-700 shadow-sm hover:shadow-md transition-all duration-200">
            <div className="border-b border-slate-200 dark:border-slate-700">
              <nav className="-mb-px flex space-x-8 px-6">
                {[
                  { id: 'overview', name: 'Overview', icon: DocumentTextIcon },
                  { id: 'chapters', name: 'Chapters', icon: BookOpenIcon },
                  { id: 'objectives', name: 'Learning Objectives', icon: ChartBarIcon }
                ].map((tab) => (
                  <button
                    key={tab.id}
                    onClick={() => setActiveTab(tab.id)}
                    className={`py-4 px-1 border-b-2 font-medium text-sm transition-colors duration-200 ${
                      activeTab === tab.id
                        ? 'border-brand-500 text-brand-600 dark:text-brand-400'
                        : 'border-transparent text-slate-500 dark:text-slate-400 hover:text-slate-700 dark:hover:text-slate-200 hover:border-slate-300 dark:hover:border-slate-600'
                    }`}
                  >
                    <tab.icon className="h-5 w-5 inline mr-2" />
                    {tab.name}
                  </button>
                ))}
              </nav>
            </div>

            <div className="p-4 md:p-5">
              {/* Overview Tab */}
              {activeTab === 'overview' && (
                <div className="space-y-6">
                  <div>
                    <h3 className="text-xl font-semibold text-slate-900 dark:text-slate-100 mb-3">About This Book</h3>
                    <p className="text-base leading-relaxed text-slate-700 dark:text-slate-200">
                      {book.long_description || book.description || 'No detailed description available.'}
                    </p>
                  </div>

                  {book.prerequisites && book.prerequisites.length > 0 && (
                    <div>
                      <h3 className="text-xl font-semibold text-slate-900 dark:text-slate-100 mb-3">Prerequisites</h3>
                      <ul className="list-disc pl-6 space-y-1 text-base leading-relaxed text-slate-700 dark:text-slate-200">
                        {book.prerequisites.map((prereq, index) => (
                          <li key={index}>{prereq}</li>
                        ))}
                      </ul>
                    </div>
                  )}

                  {book.tags && book.tags.length > 0 && (
                    <div>
                      <h3 className="text-xl font-semibold text-slate-900 dark:text-slate-100 mb-3">Tags</h3>
                      <div className="flex flex-wrap gap-2">
                        {book.tags.map((tag, index) => (
                          <span
                            key={index}
                            className="inline-flex items-center px-3 py-1 rounded-full text-sm font-medium bg-brand-100 dark:bg-brand-900/20 text-brand-800 dark:text-brand-200"
                          >
                            {tag}
                          </span>
                        ))}
                      </div>
                    </div>
                  )}
                </div>
              )}

              {/* Chapters Tab */}
              {activeTab === 'chapters' && (
                <div className="space-y-4">
                  <h3 className="text-xl font-semibold text-slate-900 dark:text-slate-100 mb-4">
                    Course Content ({chapters.length} chapters)
                  </h3>
                  {chapters.map((chapter, index) => (
                    <div
                      key={chapter.id}
                      className="border border-slate-200 dark:border-slate-700 rounded-lg p-4 hover:border-brand-300 dark:hover:border-brand-600 transition-colors duration-200"
                    >
                      <div className="flex items-center justify-between">
                        <div className="flex items-center space-x-4">
                          <div className="w-8 h-8 bg-brand-100 dark:bg-brand-900/20 text-brand-600 dark:text-brand-400 rounded-full flex items-center justify-center text-sm font-medium">
                            {index + 1}
                          </div>
                          <div>
                            <h4 className="font-medium text-slate-900 dark:text-slate-100">{chapter.title}</h4>
                            <p className="text-sm text-base leading-relaxed text-slate-700 dark:text-slate-200">{chapter.description}</p>
                            <div className="flex items-center space-x-4 mt-2 text-sm text-slate-500 dark:text-slate-400">
                              <span className="flex items-center">
                                <ClockIcon className="h-4 w-4 mr-1" />
                                {chapter.duration}
                              </span>
                              <span className="flex items-center">
                                <DocumentTextIcon className="h-4 w-4 mr-1" />
                                {chapter.sections_count || chapter.sections?.length || 0} sections
                              </span>
                            </div>
                          </div>
                        </div>
                        <Link
                          to={`/app/library/${bookId}/chapter/${chapter.id}`}
                          className="bg-brand-600 text-white px-4 py-2 rounded-lg text-sm font-medium hover:bg-brand-700 focus:ring-2 focus:ring-brand-500 focus:ring-offset-2 transition-colors duration-200"
                        >
                          Start Chapter
                        </Link>
                      </div>
                    </div>
                  ))}
                </div>
              )}

              {/* Learning Objectives Tab */}
              {activeTab === 'objectives' && (
                <div className="space-y-6">
                  <div>
                    <h3 className="text-xl font-semibold text-slate-900 dark:text-slate-100 mb-3">What You'll Learn</h3>
                    <ul className="space-y-3">
                      {book.learning_objectives?.map((objective, index) => (
                        <li key={index} className="flex items-start">
                          <div className="flex-shrink-0 w-6 h-6 bg-green-100 dark:bg-green-900/20 text-green-600 dark:text-green-400 rounded-full flex items-center justify-center text-sm font-medium mr-3 mt-0.5">

                          </div>
                          <span className="text-base leading-relaxed text-slate-700 dark:text-slate-200">{objective}</span>
                        </li>
                      ))}
                    </ul>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Sidebar */}
        <div className="space-y-6">
          {/* Start Learning Card */}
          <div className="bg-white dark:bg-slate-800 rounded-xl p-4 md:p-5 border border-slate-200 dark:border-slate-700 shadow-sm hover:shadow-md transition-all duration-200">
            <h3 className="text-xl font-semibold text-slate-900 dark:text-slate-100 mb-4">Start Learning</h3>
            <Link
              to={`/app/library/${bookId}/chapter/1`}
              className="w-full bg-brand-600 text-white py-3 px-4 rounded-lg text-center font-medium hover:bg-brand-700 focus:ring-2 focus:ring-brand-500 focus:ring-offset-2 transition-colors duration-200 mb-4 block"
            >
              <PlayIcon className="h-5 w-5 inline mr-2" />
              Begin Course
            </Link>
            <p className="text-sm text-base leading-relaxed text-slate-700 dark:text-slate-200 text-center">
              Free access to first chapter
            </p>
          </div>

          {/* Course Info */}
          <div className="bg-white dark:bg-slate-800 rounded-xl p-4 md:p-5 border border-slate-200 dark:border-slate-700 shadow-sm hover:shadow-md transition-all duration-200">
            <h3 className="text-xl font-semibold text-slate-900 dark:text-slate-100 mb-4">Course Information</h3>
            <div className="space-y-3">
              <div className="flex items-center text-sm text-base leading-relaxed text-slate-700 dark:text-slate-200">
                <UserIcon className="h-5 w-5 mr-3 text-slate-400" />
                <span>Instructor: {book.author || 'Unknown Author'}</span>
              </div>
              <div className="flex items-center text-sm text-base leading-relaxed text-slate-700 dark:text-slate-200">
                <CalendarIcon className="h-5 w-5 mr-3 text-slate-400" />
                <span>Last updated: {book.last_updated || 'Unknown'}</span>
              </div>
              <div className="flex items-center text-sm text-base leading-relaxed text-slate-700 dark:text-slate-200">
                <GlobeAltIcon className="h-5 w-5 mr-3 text-slate-400" />
                <span>Language: {book.language || 'Unknown'}</span>
              </div>
              <div className="flex items-center text-sm text-base leading-relaxed text-slate-700 dark:text-slate-200">
                <BookOpenIcon className="h-5 w-5 mr-3 text-slate-400" />
                <span>Pages: {book.total_pages || 'Unknown'}</span>
              </div>
            </div>
          </div>

          {/* Rating */}
          <div className="bg-white dark:bg-slate-800 rounded-xl p-4 md:p-5 border border-slate-200 dark:border-slate-700 shadow-sm hover:shadow-md transition-all duration-200">
            <h3 className="text-xl font-semibold text-slate-900 dark:text-slate-100 mb-4">Student Rating</h3>
            <div className="text-center">
              <div className="text-3xl font-bold text-brand-600 dark:text-brand-400 mb-2">{book.rating || 'N/A'}</div>
              <div className="flex items-center justify-center mb-2">
                {[1, 2, 3, 4, 5].map((star) => (
                  <StarIcon
                    key={star}
                    className={`h-5 w-5 ${
                      star <= Math.floor(book.rating || 0)
                        ? 'text-yellow-400 fill-current'
                        : 'text-slate-300 dark:text-slate-600'
                    }`}
                  />
                ))}
              </div>
              <p className="text-sm text-base leading-relaxed text-slate-700 dark:text-slate-200">
                Based on {(book.students_enrolled || 0).toLocaleString()} student ratings
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default BookDetailPage;
